    // address of contract
    string contract = "0x849131560A7a178F9D04cad2d681a55c51283fC3";

    string chain = "ethereum";

    string network = "ropsten";

    public async Task<string> returnMyNft()
    {
        string account = PlayerPrefs.GetString("Account");

        // set network mainnet, testnet
        // smart contract method to call
        string method = "getUserTokenWithAddress";

        // array of arguments for contract
        string args = "[\"" + account + "\"]";

        // connects to user's browser wallet to call a transaction
        string response =
            await EVM.Call(chain, network, contract, abi, method, args);

        return response;
    }

    public async Task<string> returnNftURI(string tokenId)
    {
        string uri = await ERC721.URI(chain, network, contract, tokenId);
        return uri;
    }

